'{
  "data": {
    "name": "dataset"
  },
  "spacing": 50,
  "padding": {
    "left": 20,
    "right": 0,
    "top": 20,
    "bottom": 0
  },
  "vconcat": [
    {
      "title": {
        "text": "Player total Points",
        "subtitle": "Top 10 Players"
      },
      "height": 350,
      "width": 500,
      "transform": [
        {
          "filter": {
            "param": "brush"
          }
        },
        {
          "aggregate": [
            {
              "op": "sum",
              "field": "Total pts by GW",
              "as": "SalesByRegion"
            }
          ],
          "groupby": [
            "Name"
          ]
        }
      ],
      "layer": [
        {
          "mark": {
            "type": "bar",
            "tooltip": true,
            "color": "#bdbcbc"
          },
          "encoding": {
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.2
              },
              "value": 1
            }
          }
        },
        {
          "mark": {
            "type": "text",
            "align": "left",
            "xOffset": 5,
            "size": 15
          },
          "encoding": {
            "text": {
              "field": "SalesByRegion",
              "type": "quantitative"
            },
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.2
              },
              "value": 1
            }
          }
        }
      ],
      "encoding": {
        "x": {
          "field": "SalesByRegion",
          "axis": null,
          "type": "quantitative"
        },
        "y": {
          "field": "Name",
          "sort": {
            "field": "SalesByRegion",
            "order": "descending",
            "op": "sum"
          }
        }
      }
    },
    {
      "title": {
        "text": "Brush Select to filter players by GW",
        "fontSize": 18,
        "subtitle": ""
      },
      "height": 150,
      "width": 600,
      "layer": [
        {
          "params": [
            {
              "name": "brush",
              "select": {
                "type": "interval",
                "encodings": [
                  "x"
                ]
              }
            }
          ],
          "mark": {
            "type": "bar",
            "point": true,
            "tooltip": true,
            "color": "#bdbcbc"
          },
          "encoding": {}
        },
        {
          "mark": {
            "type": "text",
            "yOffset": -10,
            "size": 12
          },
          "encoding": {
            "text": {
              "field": "Pts all rounds",
              "aggregate": "sum"
            },
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.2
              },
              "value": 1
            }
          }
        }
      ],
      "encoding": {
        "x": {
          "field": "round",
          "axis": {
            "title": null
          }
        },
        "y": {
          "field": "Pts all rounds",
          "aggregate": "sum",
          "axis":null
        }
      }
    }
  ]
}'

