Literal.Value:
'{
  "data": {
    "name": "dataset"
  },
  "title": {
    "text": "Most selected players",
    "anchor": "middle",
    "color": "#454545",
    "subtitle": "Percentage of managers who select each player",
    "subtitleColor": "#454545"
  },
  "layer": [
    {
      "mark": {
        "type": "arc",
        "outerRadius": 18,
        "fill": {
          "expr": "datum[''SizeTextFilter2__highlight'']  > 0 ? ''#f38732'' : ''#fff9ef''"
        },
        "tooltip": true
      },
      "encoding": {
        "theta": {
          "field": "sel%",
          "type": "quantitative",
          "scale": {
            "domain": [
              0,
              100
            ]
          }
        },
        "y": {
          "field": "sel%",
          "type": "quantitative",
          "axis": null
        },
        "x": {
          "field": "Name",
          "type": "nominal",
          "axis": null
        }
      }
    },
    {
      "transform": [
        {
          "calculate": "100",
          "as": "_smallC"
        }
      ],
      "mark": {
        "type": "arc",
        "outerRadius": {
          "expr": "datum[''SizeTextFilter2__highlight'']  > 0 ? 18 : 5"
        },
        "innerRadius": {
          "expr": "datum[''SizeTextFilter2__highlight'']  > 0 ? 17.3 : 0"
        },
        "color": {
          "expr": "datum[''SizeTextFilter2__highlight'']  > 0 ? ''#454545'' : ''#e3e3e3''"
        },
        "tooltip": true
      },
      "encoding": {
        "theta": {
          "field": "_smallC",
          "type": "quantitative"
        },
        "y": {
          "field": "sel%",
          "type": "quantitative",
          "axis": null
        },
        "x": {
          "field": "Name",
          "type": "nominal",
          "axis": null
        }
      }
    },
    {
      "transform": [
        {
          "calculate": "datum[''sel%'']/100",
          "as": "_label"
        },
        {
          "calculate": "45",
          "as": "_offset"
        }
      ],
      "mark": {
        "type": "text",
        "radius": 0,
        "fontSize": {
          "expr": "datum[''SizeTextFilter2__highlight'']  > 0 ? 12 : 0"
        },
        "fontWeight": 600,
        "color": "#000000"
      },
      "encoding": {
        "text": {
          "field": "_label",
          "format": ",.0%"
        },
        "theta": {
          "field": "_offset",
          "type": "quantitative"
        },
        "y": {
          "field": "sel%",
          "type": "quantitative",
          "axis": null
        },
        "x": {
          "field": "Name",
          "type": "nominal",
          "axis": null
        }
      }
    },
    {
      "params": [
        {
          "name": "sue",
          "select": {
            "type": "point",
            "on": "pointerover"
          }
        }
      ],
      "mark": {
        "type": "text",
        "radius": 0,
        "color": "#000000",
        "yOffset": -40,
        "fontWeight":600
      },
      "encoding": {
        "text": {
          "field": "Name"
        },
        "y": {
          "field": "sel%",
          "type": "quantitative",
          "axis": null
        },
        "x": {
          "field": "Name",
          "type": "nominal",
          "axis": null
        },
        "size": {
          "condition": [
            {
              "param": "sue",
              "empty": false,
              "value": 12
            }
          ],
          "value": 0
        }
      }
    },
    {

      "mark": {
        "type": "text",
        "radius": 0,
        "color": "#000000",
        "yOffset": -25
      },
      "encoding": {
        "text": {
          "field": "Total Points"
        },
        "y": {
          "field": "sel%",
          "type": "quantitative",
          "axis": null
        },
        "x": {
          "field": "Name",
          "type": "nominal",
          "axis": null
        },
        "size": {
          "condition": [
            {
              "param": "sue",
              "empty": false,
              "value": 12
            }
          ],
          "value": 0
        }
      }
    }
  ]
}'

