'{
  "data": {
    "name": "dataset"
  },  
  "padding": {
    "left": 10,
    "right": 50,
    "top": 10,
    "bottom": 0
  },
  "title": {
    "text": "Cumulative Points Total",
    "fontSize": 18,
    "subtitleFontSize": 16,
    "color": "#454545",
    "subtitle": "Click values to see extra info",
    "subtitleColor": "#454545",
    "offset": 20,
    "subtitlePadding": 5
  },
  "layer": [
    {
      "mark": {
        "type": "rule",
        "size": 2
      },
      "encoding": {
        "opacity": {
          "condition": {
            "test": {
              "field": "__selected__",
              "equal": "on"
            },
            "value": 0.85
          },
          "value": 0
        },
        "y": {
          "field": "Total pts by GW ALL round",
          "type": "quantitative",
          "axis": null
        },
        "y2": {
          "field": "Total pts cum"
        }
      }
    },
    {
      "params": [
        {
          "name": "startRound",
          "select": {
            "type": "point",
            "fields": [
              "round"
            ]
          }
        }
      ],
      "transform": [
        {
          "filter": "isValid(startRound.round) ? datum.round >= startRound.round : true"
        }
      ],
      "mark": {
        "type": "rect",
        "width": {
          "band": 1.003
        }
      },
      "encoding": {
        "opacity": {
          "condition": {
            "test": {
              "field": "__selected__",
              "equal": "off"
            },
            "value": 0.1
          },
          "value": 0
        },
        "y": {
          "field": "Total pts by GW ALL round",
          "type": "quantitative",
          "axis": null
        }
      }
    },
    {
      "mark": {
        "type": "bar",
        "opacity": 0.5,

        "width": {
          "band": 0.75
        },
        "color": "#454545"
      },
      "encoding": {}
    },
    {
      "mark": {
        "type": "bar",

        "width": {
          "band": 0.75
        },
        "color": "#454545"
      },
      "encoding": {
        "opacity": {
          "condition": {
            "test": {
              "field": "__selected__",
              "equal": "off"
            },
            "value": 0
          },
          "value": 0.85
        }
      }
    },
    { //Cum total text
      "mark": {
        "type": "text",
        "dy": -60,
        "size": 16,
        "lineBreak": "-",
        "align":"right",
        "tooltip":true
      },
      "transform": [
        {
          "calculate": "''Grand total: ''+ format(datum[''Total pts by GW ALL round''],'',.0f'') +''-'' + ''Cumulative Total: '' + format(datum[''Total pts cum''], '',.0f'') +''-'' + ''Week Total: '' + format(datum[''Total pts by GW''], '',.0f'')",
          "as": "_counter"
        }
      ],
      "encoding": {
        "text": {
          "field": "_counter"
        },
        "opacity": {
          "condition": {
            "test": {
              "field": "__selected__",
              "equal": "on"
            },
            "value": 1
          },
          "value": 0
        },
        "y": {
          "field": "Total pts by GW ALL round",
          "type": "quantitative",
          "axis": null
        }
      }
    },
    { //Cum total text
      "mark": {
        "type": "text",
        "dy": -8,
        "size": 14
      },
      "encoding": {
        "text": {
          "field": "Total pts cum",
          "format":",.2s"
        },
        "opacity": {
          "condition": {
            "test": {
              "field": "__selected__",
              "equal": "on"
            },
            "value": 0
          },
          "value": 1
        },
        "y": {
          "field": "Total pts cum",
          "type": "quantitative",
          "axis": null
        }
      }
    }
  ],
  "encoding": {
    "x": {
      "field": "round",
      "type": "nominal"
    },
    "y": {
      "type": "quantitative",
      "field": "Total pts cum",
      "axis": null
    }
  }
}'

