'{      "title": {
        "text": "Recreating visuals is fun",
        "anchor": "start",
        "fontSize": 20,
        "subtitleFontSize": 16,
        "color": "#454545",
        "subtitle": "Find something you want to make, fake some data, build!",
        "subtitleColor": "#454545",
        "offset": 20,
        "subtitlePadding": 5
      },
  "data": {
    "name": "dataset" // data is passed in from outside
  },
  "spacing": 50, // gap between small multiples
  "columns": 3, // number of columns in the concat grid
  "padding": {
    "top": 25,
    "left": 25
  },
  "concat": [
    // ─────────────────────────────────────────────
    // 1) Vertical bars with zero line
    // ─────────────────────────────────────────────
    {
      "height": 100,
      "width": 150,
      "layer": [
        {
          "mark": {
            "type": "bar",
            "width": {
              "band": 0.8 // bar thickness relative to band
            },
            "color": {
              // green if x_ >= 0, red otherwise
              "expr": "datum[''x_'']>= 0 ?  ''#8db601'':''#fd0008''"
            }
          },
          "encoding": {
            "x": {
              "field": "Y",
              "type": "nominal",
              "axis": null // hide x-axis for this panel
            },
            "y": {
              "field": "x_",
              "type": "quantitative",
              "axis": null // hide y-axis for this panel
            },
            "opacity": {
              // dim when point is not selected
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.1
              },
              "value": 1
            }
          }
        },
        {
          "mark": {
            "type": "rule",
            "color": "#a7a4a8",
            "strokeWidth": 5 // THICK zero line
          },
          "encoding": {
            "y": {
              "datum": 0 // horizontal line at 0
            }
          }
        }
      ]
    },
    // ─────────────────────────────────────────────
    // 2) Area chart with zero line and selection-driven opacity
    // ─────────────────────────────────────────────
    {
      "height": 100,
      "width": 150,
      "layer": [
        {
          "mark": {
            "type": "rule"
          },
          "encoding": {
            "x": {
              "field": "Y",
              "type": "nominal",
              "axis": null
            },
            "y": {
              "field": "x_",
              "type": "quantitative",
              "axis": null
            },
            "opacity": {
              // only visible when __selected__ = "on"
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "on"
                },
                "value": 1
              },
              "value": 0
            }
          }
        },
        {
          "mark": {
            "type": "area",
            "color": {
              // vertical gradient (y1=1 bottom, y2=0 top)
              "gradient": "linear",
              "x1": 0,
              "y1": 1,
              "x2": 0,
              "y2": 0,
              "stops": [
                {
                  "offset": 0.23,
                  "color": "#fa1c1c" // red below pivot
                },
                {
                  "offset": 0,
                  "color": "#8eb632" // green above pivot
                }
              ]
            },
            "clip": true // keep area within plot
          },
          "encoding": {
            "x": {
              "field": "Y",
              "type": "nominal",
              "axis": null
            },
            "y": {
              "field": "x_",
              "type": "quantitative",
              "axis": null
            },
            "opacity": {
              // dim when not selected
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.1
              },
              "value": 1
            }
          }
        },
        {
          "mark": {
            "type": "rule",
            "color": "#454545",
            "strokeWidth": 5 // thick zero line
          },
          "encoding": {
            "y": {
              "datum": 0
            }
          }
        }
      ]
    },
    // ─────────────────────────────────────────────
    // 3) Horizontal bars (x quantitative) with vertical zero line
    // ─────────────────────────────────────────────
    {
      "height": 100,
      "width": 150,
      "layer": [
        {
          "mark": {
            "type": "bar",
            "height": {
              "band": 0.8
            },
            "color": {
              "expr": "datum[''x_'']>= 0 ?  ''#8db601'':''#fd0008''"
            }
          },
          "encoding": {
            "y": {
              "field": "Y",
              "type": "nominal",
              "axis": null
            },
            "x": {
              "field": "x_",
              "type": "quantitative",
              "axis": null
            },
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.1
              },
              "value": 1
            }
          }
        },
        {
          "mark": {
            "type": "rule",
            "color": "#a7a4a8",
            "strokeWidth": 5
          },
          "encoding": {
            "x": {
              "datum": 0 // vertical line at 0
            }
          }
        }
      ]
    },
    // ─────────────────────────────────────────────
