      "height": 100,
      "width": 150,
      "layer": [
        {
          // dark background squares
          "mark": {
            "type": "square",
            "color": "#454545",
            "size": 150
          },
          "encoding": {
            "x": {
              "field": "Y",
              "type": "nominal",
              "axis": null
            },
            "y": {
              "field": "x_",
              "type": "quantitative",
              "axis": null
            },
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.1
              },
              "value": 1
            }
          }
        },
        {
          // narrow colored bar in the center
          "mark": {
            "type": "bar",
            "width": {
              "band": 0.15
            },
            "color": {
              "expr": "datum[''x_'']>= 0 ?  ''#8db601'':''#fd0008''"
            }
          },
          "encoding": {
            "x": {
              "field": "Y",
              "type": "nominal",
              "axis": null
            },
            "y": {
              "field": "x_",
              "type": "quantitative",
              "axis": null
            },
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.1
              },
              "value": 1
            }
          }
        },
        {
          // thick dark zero line
          "mark": {
            "type": "rule",
            "color": "#454545",
            "strokeWidth": 5
          },
          "encoding": {
            "y": {
              "datum": 0
            }
          }
        },
        {
          // thin white zero line on top for contrast
          "mark": {
            "type": "rule",
            "color": "#ffffff",
            "strokeWidth": 1
          },
          "encoding": {
            "y": {
              "datum": 0
            }
          }
        }
      ]
    },
    // ─────────────────────────────────────────────
    // 5) White background + white bar (placeholder version)
    // ─────────────────────────────────────────────
    {
      "height": 100,
      "width": 150,
      "layer": [
        {
          "mark": {
            "type": "square",
            "color": "#ffffff",
            "size": 150
          },
          "encoding": {
            "x": {
              "field": "Y",
              "type": "nominal",
              "axis": null
            },
            "y": {
              "field": "x_",
              "type": "quantitative",
              "axis": null
            },
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.1
              },
              "value": 1
            }
          }
        },
        {
          "mark": {
            "type": "bar",
            "width": {
              "band": 0.15
            },
            // simplified: previously an expression always returning white
            "color": "#ffffff"
          },
          "encoding": {
            "x": {
              "field": "Y",
              "type": "nominal",
              "axis": null
            },
            "y": {
              "field": "x_",
              "type": "quantitative",
              "axis": null
            },
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.1
              },
              "value": 1
            }
          }
        }
      ]
    },
    // ─────────────────────────────────────────────
    // 6) Horizontal lollipop-style bars
    // ─────────────────────────────────────────────
    {
      "height": 100,
      "width": 150,
      "layer": [
        {
          "mark": {
            "type": "square",
            "color": "#454545",
            "size": 150
          },
          "encoding": {
            "y": {
              "field": "Y",
              "type": "nominal",
              "axis": null
            },
            "x": {
              "field": "x_",
              "type": "quantitative",
              "axis": null
            },
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.1
              },
              "value": 1
            }
          }
        },
        {
          "mark": {
            "type": "bar",
            "height": {
              "band": 0.25
            },
            "color": {
              "expr": "datum[''x_'']>= 0 ?  ''#8db601'':''#fd0008''"
            }
          },
          "encoding": {
            "y": {
              "field": "Y",
              "type": "nominal",
              "axis": null
            },
            "x": {
              "field": "x_",
              "type": "quantitative",
              "axis": null
            },
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.1
              },
              "value": 1
            }
          }
        },
        {
          "mark": {
            "type": "rule",
            "color": "#454545",
            "strokeWidth": 5
          },
          "encoding": {
            "x": {
              "datum": 0
            }
          }
        },
        {
          "mark": {
            "type": "rule",
            "color": "#ffffff",
            "strokeWidth": 1
          },
          "encoding": {
            "x": {
              "datum": 0
            }
          }
        }
      ]
    },
    // ─────────────────────────────────────────────
    // 7) Vertical waterfall chart (x = category)
    // ─────────────────────────────────────────────
    {
      "width": 150,
      "height": 100,
      "transform": [
        {
          // cumulative sum over x_wf to build waterfall
          "window": [
            {
              "op": "sum",
              "field": "x_wf",
              "as": "sum"
            }
          ]
        },
        {
          // lead(Y) used to draw connectors to next bar
          "window": [
            {
              "op": "lead",
              "field": "Y",
              "as": "lead"
            }
          ]
        },
        {
          // replace null lead with current Y (for last element)
          "calculate": "datum.lead === null ? datum.Y : datum.lead",
          "as": "lead"
        },
        {
          // bottom of each bar = previous cumulative sum
          "calculate": "datum.Y === 5 ? 0 : datum.sum - datum.x_",
          "as": "previous_sum"
        }
        // NOTE: removed unused x_, text_x_, center calculations
      ],
      "encoding": {
        "x": {
          "field": "Y",
          "type": "ordinal",
          "sort": null,
          "axis": {
            "labelFontSize": 0, // hide labels
            "domainWidth": 2 // THICK x-axis domain line
          }
        }
      },
      "layer": [
        {
          "mark": {
            "type": "bar",
            "size": 15
          },
          "encoding": {
            "y": {
              "field": "previous_sum",
              "type": "quantitative",
              "title": "x_" // label only, not data reference
            },
            "y2": {
              "field": "sum"
            },
            "color": {
              // special color for first/last and negative changes
              "condition": [
                {
                  "test": "datum.Y === 1 || datum.Y === 5",
                  "value": "#454545"
                },
                {
                  "test": "datum.sum < datum.previous_sum",
                  "value": "#fa1c1c"
                }
              ],
              "value": "#8eb632"
            }
          }
        },
        {
          // connector lines between bars
          "mark": {
            "type": "rule",
            "color": "#404040",
            "opacity": 1,
            "strokeWidth": 0.3,
            "xOffset": -7,
            "x2Offset": 7
          },
          "encoding": {
            "x2": {
              "field": "lead"
            },
            "y": {
              "field": "sum",
              "type": "quantitative",
              "axis": null
            }
          }
        }
      ]
    },
    // ─────────────────────────────────────────────
    // 8) Another white placeholder lollipop-style chart
    // (same structure as 5)
    // ─────────────────────────────────────────────
    {
      "height": 100,
      "width": 150,
      "layer": [
        {
          "mark": {
            "type": "square",
            "color": "#ffffff",
            "size": 150
          },
          "encoding": {
            "x": {
              "field": "Y",
              "type": "nominal",
              "axis": null
            },
            "y": {
              "field": "x_",
              "type": "quantitative",
              "axis": null
            },
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.1
              },
              "value": 1
            }
          }
        },
        {
          "mark": {
            "type": "bar",
            "width": {
              "band": 0.15
            },
            "color": "#ffffff" // simplified constant color
          },
          "encoding": {
            "x": {
              "field": "Y",
              "type": "nominal",
              "axis": null
            },
            "y": {
              "field": "x_",
              "type": "quantitative",
              "axis": null
            },
            "opacity": {
              "condition": {
                "test": {
                  "field": "__selected__",
                  "equal": "off"
                },
                "value": 0.1
              },
              "value": 1
            }
          }
        }
      ]
    },
    // ─────────────────────────────────────────────
    // 9) Horizontal waterfall chart (y = category)
    // ─────────────────────────────────────────────
    {
      "width": 150,
      "height": 100,
      "transform": [
        {
          "window": [
            {
              "op": "sum",
              "field": "x_wf",
              "as": "sum"
            }
          ]
        },
        {
          "window": [
            {
              "op": "lead",
              "field": "Y",
              "as": "lead"
            }
          ]
        },
        {
          "calculate": "datum.lead === null ? datum.Y : datum.lead",
          "as": "lead"
        },
        {
          "calculate": "datum.Y === 5 ? 0 : datum.sum - datum.x_",
          "as": "previous_sum"
        }
        // removed unused x_, text_x_, center again
      ],
      "encoding": {
        "y": {
          "field": "Y",
          "type": "ordinal",
          "sort": null,
          "axis": {
            "labelFontSize": 0,
            "domainWidth": 2 // THICK y-axis domain line here
          }
        }
      },
      "layer": [
        {
          "mark": {
            "type": "bar",
            "size": 15
          },
          "encoding": {
            "x": {
              "field": "previous_sum",
              "type": "quantitative",
              "title": "x_"
            },
            "x2": {
              "field": "sum"
            },
            "color": {
              "condition": [
                {
                  "test": "datum.Y === 1 || datum.Y === 5",
                  "value": "#454545"
                },
                {
                  "test": "datum.sum < datum.previous_sum",
                  "value": "#fa1c1c"
                }
              ],
              "value": "#8eb632"
            }
          }
        },
        {
          "mark": {
            "type": "rule",
            "color": "#404040",
            "opacity": 1,
            "strokeWidth": 0.3,
            "yOffset": -7,
            "y2Offset": 7
          },
          "encoding": {
            "y2": {
              "field": "lead"
            },
            "x": {
              "field": "sum",
              "type": "quantitative",
              "axis": null
            }
          }
        }
      ]
    }
  ]
